import React from "react";

class InputForm extends React.Component {
  constructor() {
    super();

    this.state = {
      name: "",
      blockchain: "",
      key: "",
    };

    this.updateName = this.updateName.bind(this);
  }

  updateName(value) {
    this.setState({
      name: value,
    });
  }

  updateBlockchain(value) {
    this.setState({
      blockchain: value,
    });
  }

  updateKey(value) {
    this.setState({
      key: value,
    });
  }

  render() {
    return (
      <div>
        <form action="">
          <input type="text" placeholder="Wallet Name" />
          <select name="Blockchain">
            <option value="empty"></option>
            <option value="bitcoin">Bitcoin (BTC)</option>
            <option value="ethereum">Ethereum (ETH)</option>
            <option value="binance">Binance (BNB)</option>
          </select>
          <input type="text" placeholder="Public Key" />
          <button type="submit">Submit</button>
        </form>
      </div>
    );
  }
}

export default InputForm;
