import React from "react";
import Popup from "reactjs-popup";

const About = () => (
  <Popup trigger={<div> About </div>} modal nested>
    {(close) => (
      <div className="bg-dark-grey border-round-s border-color-augment font-color-white pad-2">
        <div className="flex-row-align disperse">
          <div className="wide-font"> About </div>
          <button onClick={close}>&times;</button>
        </div>
        <div className="mono-font margin-top-2">Welcome to Portal Wallet!</div>
      </div>
    )}
  </Popup>
);

export default About;
