import React from "react";

class Style extends React.Component {
  constructor() {
    super();
    this.state = {
      active: "mono",
    };

    this.handleActive = this.handleActive.bind(this);
  }

  handleActive(value) {
    this.setState({ active: value });
  }

  render() {
    return (
      <form
        className="flex-row-align standard-font gap-05 pad-2"
        onChange={(event) => {
          this.props.updateStyle(event.target.value);
        }}
      >
        <div
          onClick={this.props.updateStyle("mono")}
          className={`border-round-xs pad-05 border-color-augment mono-font ${
            this.state.active === "mono" ? "style-active" : ""
          }`}
        >
          <input
            type="radio"
            value="mono"
            name="style"
            onChange={(event) => {
              this.handleActive(event.target.value);
            }}
          />
          Mono
        </div>
        <div
          onClick={this.props.updateStyle("neomorphic")}
          className={`border-round-xs pad-05 border-color-augment mono-font ${
            this.state.active === "neomorphic" ? "style-active" : ""
          }`}
        >
          <input
            type="radio"
            value="neomorphic"
            name="style"
            onChange={(event) => {
              this.handleActive(event.target.value);
            }}
          />
          Neomorphic
        </div>
        <div
          onClick={this.props.updateStyle("gradient")}
          className={`border-round-xs pad-05 border-color-augment mono-font ${
            this.state.active === "gradient" ? "style-active" : ""
          }`}
        >
          <input
            type="radio"
            value="gradient"
            name="style"
            onChange={(event) => {
              this.handleActive(event.target.value);
            }}
          />
          Gradient
        </div>
        <div
          onClick={this.props.updateStyle("holo")}
          className={`border-round-xs pad-05 border-color-augment mono-font ${
            this.state.active === "holo" ? "style-active" : ""
          }`}
        >
          <input
            type="radio"
            value="holo"
            name="style"
            onChange={(event) => {
              this.handleActive(event.target.value);
            }}
          />
          Holo
        </div>
      </form>
    );
  }
}

export default Style;
