import React from "react";

class Style extends React.Component {
  constructor() {
    super();
    this.state = {
      active: "mono",
    };

    this.handleActive = this.handleActive.bind(this);
  }

  handleActive(event) {
    this.setState({ active: event.target.value });
  }

  render() {
    return (
      <div
        className="flex-row-align standard-font gap-05 pad-2"
        onChange={(event) => {
          this.props.updateStyle(event.target.value);
          this.handleActive(event);
        }}
      >
        <div className="border-round-xs pad-05 border-color-augment mono-font">
          <input
            type="radio"
            value="mono"
            name="style"
            checked={active === "mono"}
          />
          Mono
        </div>
        <div className="border-round-xs pad-05 border-color-augment mono-font">
          <input
            type="radio"
            value="neomorphic"
            name="style"
            checked={active === "neomorphic"}
          />
          Neomorphic
        </div>
        <div className="border-round-xs pad-05 border-color-augment mono-font">
          <input
            type="radio"
            value="gradient"
            name="style"
            checked={active === "gradient"}
          />
          Gradient
        </div>
        <div className="border-round-xs pad-05 border-color-augment mono-font">
          <input
            type="radio"
            value="holo"
            name="style"
            checked={active === "holo"}
          />
          Holo
        </div>
      </div>
    );
  }
}

export default Style;
