import React from "react";

class InputForm extends React.Component {
  constructor() {
    super();

    this.state = {
      name: "",
      blockchain: "",
      key: "",
    };

    this.updateName = this.updateName.bind(this);
    this.updateBlockchain = this.updateName.bind(this);
    this.updateKey = this.updateName.bind(this);
  }

  updateName(value) {
    this.setState({
      name: value,
    });
  }

  updateBlockchain(value) {
    this.setState({
      blockchain: value,
    });
  }

  updateKey(value) {
    this.setState({
      key: value,
    });
  }

  render() {
    return (
      <div>
        <form action="">
          <input
            type="text"
            placeholder="Wallet Name"
            onChange={(event) => this.updateName(event.target.value)}
          />
          <select
            name="Blockchain"
            onChange={(event) => this.updateBlockchain(event.target.value)}
          >
            <option value="empty"></option>
            <option value="bitcoin">Bitcoin (BTC)</option>
            <option value="ethereum">Ethereum (ETH)</option>
            <option value="binance">Binance (BNB)</option>
          </select>
          <input
            type="text"
            placeholder="Public Key"
            onChange={(event) => this.updateKey(event.target.value)}
          />
        </form>
      </div>
    );
  }
}

export default InputForm;
