import "./App.css";
import React, { createRef } from "react";
import Header from "./components/Header";
import Form from "./components/Form";
import Style from "./components/Style";
import Wallet from "./components/Wallet";

class App extends React.Component {
  constructor(props) {
    super(props);
    const walletRef = createRef();
    this.state = {
      name: "",
      blockchain: "",
      address: "",
      style: "mono",
      walletNode: "",
    };

    this.updateName = this.updateName.bind(this);
    this.updateBlockchain = this.updateBlockchain.bind(this);
    this.updateAddress = this.updateAddress.bind(this);
    this.updateStyle = this.updateStyle.bind(this);
    this.updateWalletNode = this.updateWalletNode.bind(this);
  }

  updateName(value) {
    this.setState({
      name: value,
    });
  }

  updateBlockchain(value) {
    this.setState({
      blockchain: value,
    });
  }

  updateAddress(value) {
    this.setState({
      address: value,
    });
  }

  updateStyle(value) {
    this.setState({ style: value });
  }

  updateWalletNode(walletRef) {
    this.setState({ walletNode: walletRef });
  }

  render() {
    return (
      <div className="flex-column">
        <Header walletNode={this.state.walletNode} />
        <div className="flex-row">
          <div className="bg-dark-grey flex-column fill-height">
            <Form
              updateName={this.updateName}
              updateBlockchain={this.updateBlockchain}
              updateAddress={this.updateAddress}
            />
            <Style updateStyle={this.updateStyle} style={this.state.style} />
          </div>
          <div className="bg-dark fill-width">
            <Wallet
              name={this.state.name}
              blockchain={this.state.blockchain}
              address={this.state.address}
              style={this.state.style}
              ref={this.walletRef}
              updateWalletNode={this.updateWalletNode}
            />
          </div>
        </div>
      </div>
    );
  }
}

export default App;
