import React from "react";

class Style extends React.Component {
  constructor() {
    super();
    this.state = {
      active: "mono",
    };

    this.handleActive = this.handleActive.bind(this);
  }

  handleActive(value) {
    this.setState({ active: value });
  }

  render() {
    return (
      <form
        className="flex-row-align standard-font gap-05 pad-2"
        onChange={(event) => {
          this.props.updateStyle(event.target.value);
        }}
      >
        <input
          type="radio"
          value="mono"
          name="style"
          checked={this.state.active === "mono"}
          onChange={(event) => {
            this.handleActive(event.target.value);
          }}
          className="border-round-xs pad-05 border-color-augment mono-font style-input"
        />
        Mono
        <input
          type="radio"
          value="neomorphic"
          name="style"
          checked={this.state.active === "neomorphic"}
          onChange={(event) => {
            this.handleActive(event.target.value);
          }}
          className="border-round-xs pad-05 border-color-augment mono-font style-input"
        />
        Neomorphic
        <input
          type="radio"
          value="gradient"
          name="style"
          checked={this.state.active === "gradient"}
          onChange={(event) => {
            this.handleActive(event.target.value);
          }}
          className="border-round-xs pad-05 border-color-augment mono-font style-input"
        />
        Gradient
        <input
          type="radio"
          value="holo"
          name="style"
          checked={this.state.active === "holo"}
          onChange={(event) => {
            this.handleActive(event.target.value);
          }}
          className="border-round-xs pad-05 border-color-augment mono-font style-input"
        />
        Holo
      </form>
    );
  }
}

export default Style;
