import "./App.css";
import React from "react";
import Header from "./components/Header";
import Form from "./components/Form";
import Style from "./components/Style";
import Wallet from "./components/Wallet";
import Submit from "./components/Submit";

class App extends React.Component {
  constructor(props) {
    super(props);

    this.state = {
      name: "",
      blockchain: "",
      address: "",
      style: "mono",
      submit: false,
    };

    this.updateName = this.updateName.bind(this);
    this.updateBlockchain = this.updateBlockchain.bind(this);
    this.updateAddress = this.updateAddress.bind(this);
    this.updateStyle = this.updateStyle.bind(this);
    this.updateWallet = this.updateWallet.bind(this);
  }

  updateName(value) {
    this.setState({
      name: value,
    });
  }

  updateBlockchain(value) {
    this.setState({
      blockchain: value,
    });
  }

  updateAddress(value) {
    this.setState({
      address: value,
    });
  }

  updateStyle(value) {
    this.setState({ style: value });
  }

  updateWallet() {
    if (this.state.address && this.state.blockchain && this.state.name) {
      this.setState({ submit: true });
    } else {
      this.setState({ submit: false });
    }
  }

  render() {
    return (
      <div className="flex-column">
        <Header />
        <div className="flex-row">
          <div className="bg-dark-grey flex-column fill-height">
            <Form
              updateName={this.updateName}
              updateBlockchain={this.updateBlockchain}
              updateAddress={this.updateAddress}
            />
            <Style updateStyle={this.updateStyle} />
            <Submit updateWallet={this.updateWallet} />
          </div>
          <div className="bg-dark fill-width">
            <Wallet
              name={this.state.name}
              blockchain={this.state.blockchain}
              address={this.state.address}
              style={this.state.style}
            />
          </div>
        </div>
      </div>
    );
  }
}

export default App;
